import { Tree } from '@angular-devkit/schematics';
import { addPackage } from './package-config';

// eslint-disable-next-line @typescript-eslint/no-var-requires
const VERSION = require('../package.json').version;

/** Add dependencies to package.json */
export function addKeyPkgsToPackageJson(host: Tree) {
  addPackage(host, '@angular/cdk@~15.0.3');
  addPackage(host, 'parse5@^7.1.2', 'dev');
  addPackage(host, `ng-matero@~${VERSION}`);
}

/** Add dependencies to package.json */
export function add3rdPkgsToPackageJson(host: Tree) {
  addPackage(host, '@angular/material@~15.0.3');
  addPackage(host, '@angular/material-moment-adapter@~15.0.3');

  // 3rd lib
  addPackage(host, '@ng-matero/extensions@^15.0.1');
  addPackage(host, '@ng-matero/extensions-moment-adapter@^15.0.0');
  addPackage(host, '@ngx-formly/core@^6.0.4');
  addPackage(host, '@ngx-formly/material@^6.0.4');
  addPackage(host, '@ngx-translate/core@^14.0.0');
  addPackage(host, '@ngx-translate/http-loader@^7.0.0');
  addPackage(host, 'moment@^2.29.4');
  addPackage(host, 'ngx-permissions@^14.0.0');
  addPackage(host, 'ngx-progressbar@^9.0.0');
  addPackage(host, 'ngx-toastr@^16.0.1');
  addPackage(host, 'photoviewer@^3.6.5');
  addPackage(host, 'screenfull@^6.0.2');

  // Dev
  addPackage(host, '@angular-eslint/builder@^15.1.0', 'dev');
  addPackage(host, '@angular-eslint/eslint-plugin@^15.1.0', 'dev');
  addPackage(host, '@angular-eslint/eslint-plugin-template@^15.1.0', 'dev');
  addPackage(host, '@angular-eslint/schematics@^15.1.0', 'dev');
  addPackage(host, '@angular-eslint/builder@0.0.0-PLACEHOLDER', 'dev');
  addPackage(host, '@angular-eslint/template-parser@^15.1.0', 'dev');
  addPackage(host, '@typescript-eslint/eslint-plugin@^5.46.0', 'dev');
  addPackage(host, '@typescript-eslint/parser@^5.46.0', 'dev');
  addPackage(host, 'eslint@^8.30.0', 'dev');
  addPackage(host, 'prettier@^2.8.1', 'dev');
  addPackage(host, 'stylelint@^14.16.0', 'dev');
  addPackage(host, 'stylelint-config-rational-order@^0.1.2', 'dev');
  addPackage(host, 'stylelint-config-recommended-scss@^8.0.0', 'dev');
  addPackage(host, 'stylelint-config-standard@^29.0.0', 'dev');
  addPackage(host, 'stylelint-order@^5.0.0', 'dev');
  addPackage(host, 'stylelint-scss@^4.3.0', 'dev');
}
